import React, { useState, useEffect } from "react";
import { Container, Row, Col, Button } from "reactstrap";
import axios from "axios";
import { Link } from 'react-router-dom'

const Bookingss = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      // Replace 'YOUR_API_URL_HERE' with the actual URL you want to fetch data from
      const bookingResponse = await axios.get('http://localhost:4000/bookings');
      setBookings(bookingResponse.data.data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching data:", error);
      setLoading(false);
    }
  };

  const handleConfirmBooking = async (bookingId) => {
    try {
      // Perform a confirmed action here, for example, send a request to your API
      // with the bookingId to mark it as confirmed.
      console.log(`Booking confirmed for ID: ${bookingId}`);
    } catch (error) {
      console.error("Error confirming booking:", error);
    }
  };

  const handleDeleteBooking = async (bookingId) => {
    try {
      // Perform a delete action here, for example, send a request to your API
      // to delete the booking with the given bookingId.
      console.log(`Booking deleted for ID: ${bookingId}`);
    } catch (error) {
      console.error("Error deleting booking:", error);
    }
  };

  return (
    <section>
      <Container>
        <Row>
          <Col lg="12">
            <h4 className="fw-bold">Bookings</h4>
          </Col>
          <Col lg="12" className="pt-5">
            <table className="table">
              <thead>
                <tr>
                  <th>User Email</th>
                  <th>Full Name</th>
                  <th>Guest Size</th>
                  <th>Phone</th>
                  <th>Booked At</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {bookings.map((booking) => (
                  <tr key={booking._id}>
                    <td>{booking.userEmail}</td>
                    <td>{booking.fullName}</td>
                    <td>{booking.guestSize}</td>
                    <td>{booking.phone}</td>
                    <td>{booking.bookedAt}</td>
                    <td>
                      <Button
                        color="success"
                        onClick={() => handleConfirmBooking(booking._id)}
                      >
                        Confirm
                      </Button>{" "}
                      <Button
                        color="danger"
                        onClick={() => handleDeleteBooking(booking._id)}
                      >
                        Delete
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Bookingss;
