import React, { useContext } from "react";
import { Navigate, Outlet } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

const ProtectedRoute = () => {
  const { isAuthenticated, user, role } = useContext(AuthContext);

  if (!isAuthenticated) {
    // A) Unauthenticated users should be redirected to the login page.
    return <Navigate to="/login" />;
  }

  if (user && role === 'admin') {
    // B) Authenticated users with the 'admin' role should be allowed access to the dashboard.
    return <Outlet />;
  }

  // C) Authenticated non-admin users should be redirected to the home page.
  return <Navigate to="/" />;
};

export default ProtectedRoute;
