// UpdateTours.jsx
import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, FormGroup, Input, Label } from 'reactstrap';
import axios from 'axios';
import { toast } from 'react-toastify';

const UpdateTours = ({ tourToUpdate, onUpdate }) => {
  const [tourData, setTourData] = useState(tourToUpdate);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTourData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send a PUT request to update the tour
      const response = await axios.put(`http://localhost:4000/tours/${tour._id}`, tourData);

      if (response.status === 200) {
        toast.success('Tour updated successfully');
        onUpdate(tourData); // Notify parent component about the updated tour
      } else {
        throw new Error('Network response was not ok');
      }
    } catch (error) {
      console.error('Error updating tour:', error);
      toast.error('Failed to update tour');
    }
  };

  return (
    <section>
      <Container>
        <Row>
          <Col lg="12">
            <h4 className="mb-5">Update Tour</h4>
            <Form onSubmit={handleSubmit}>
              {/* Form inputs go here */}
              <FormGroup>
              <FormGroup>
  <Label>Title</Label>
  <Input
    type="text"
    name="title"
    value={tourData.title}
    onChange={handleChange}
    required
  />
</FormGroup>

              </FormGroup>

              <FormGroup>
                <Label>City</Label>
                <Input
                  type="text"
                  name="city"
                  value={tourData.city}
                  onChange={handleChange}
                  required
                />
              </FormGroup>

              <FormGroup>
                <Label>Address</Label>
                <Input
                  type="text"
                  name="address"
                  value={tourData.address}
                  onChange={handleChange}
                  required
                />
              </FormGroup>

              <FormGroup>
                <Label>Distance</Label>
                <Input
                  type="number"
                  name="distance"
                  value={tourData.distance}
                  onChange={handleChange}
                  required
                />
              </FormGroup>

              <FormGroup>
                <Label>Photo (URL)</Label>
                <Input
                  type="text"
                  name="photo"
                  value={tourData.photo}
                  onChange={handleChange}
              
                />
              </FormGroup>

              <FormGroup>
                <Label>Description</Label>
                <Input
                  type="text"
                  name="desc"
                  value={tourData.desc}
                  onChange={handleChange}
                  required
                />
              </FormGroup>

              <FormGroup>
                <Label>Price</Label>
                <Input
                  type="number"
                  name="price"
                  value={tourData.price}
                  onChange={handleChange}
                  required
                />
              </FormGroup>

              <FormGroup>
                <Label>Max Group Size</Label>
                <Input
                  type="number"
                  name="maxGroupSize"
                  value={tourData.maxGroupSize}
                  onChange={handleChange}
                  required
                />
              </FormGroup>
              {/* ... */}
              <button className="buy__btn" type="submit">
                Update Tour
              </button>
            </Form>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default UpdateTours;
