import React, { useState, useEffect } from "react";
import { Container, Row, Col } from "reactstrap";
import axios from "axios";

const Bookingss = () => {
  const [bookings, setBookings] = useState([]);
  const [tours, setTours] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const bookingResponse = await axios.get(`http://localhost:4000/tours/bookings`, (req, res) => {
          // Handle the GET request for bookings here
        });
        
        const tourResponse = await axios.get(`http://localhost:4000/tours/`);
        
        setBookings(bookingResponse.data.data);
        setTours(tourResponse.data.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching data:", error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  return (
    <section>
      <Container>
        <Row>
          <Col lg="12">
            <h4 className="fw-bold">Bookings</h4>
          </Col>
          <Col lg="12" className="pt-5">
            <h4 className="fw-bold">Tours</h4>
            <ul>
              {loading ? (
                <li>Loading tours and bookings...</li>
              ) : (
                tours.map((tour) => (
                  <li key={tour._id}>{tour.name}</li>
                ))
              )}
            </ul>
            <table className="table">
              <thead>
                <tr>
                  <th>User Email</th>
                  <th>Full Name</th>
                  <th>Guest Size</th>
                  <th>Phone</th>
                  <th>Booked At</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {loading ? (
                  <tr>
                    <td colSpan="6" className="text-center">
                      <h5 className="pt-5 fw-bold">Loading bookings...</h5>
                    </td>
                  </tr>
                ) : (
                  bookings.map((booking) => (
                    <tr key={booking._id}>
                      <td>{booking.userEmail}</td>
                      <td>{booking.fullName}</td>
                      <td>{booking.guestSize}</td>
                      <td>{booking.phone}</td>
                      <td>{new Date(booking.bookAt).toLocaleString()}</td>
                      <td>
                        {/* Add action buttons here */}
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Bookingss;
