import React, { useState } from 'react';
import axios from 'axios'; // Make sure to have axios installed
import "../styles/addtours.css";
import { toast } from 'react-toastify';

const AddTours = () => {
  const [tourData, setTourData] = useState({
    title: '',
    name: '',
    price: '',
    description: '',
    image: '',
  });

  const handleChange = async(e) => {
    const { name, value } = e.target;
    setTourData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };
  const handleImageChange = async(e) => {
    const imageFile = e.target.files[0];
    setTourData((prevData) => ({
      ...prevData,
      image: imageFile,
    }));
  };
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
    
            const formData = new FormData();
            formData.append('title', tourData.title);
            formData.append('name', tourData.name);
            formData.append('price', tourData.price);
            formData.append('description', tourData.description);
            formData.append('image', tourData.image);
        const response = await axios.post(`http://localhost:4000/api/controllers/t`, formData, {
            headers: {
              'Content-Type': 'multipart/form-data', 
            },
          }); 
          
          toast.success(' tour package succesfully added ')// Adjust the API endpoint // Adjust the API endpoint
          console.log(response.data);
          setTourData({
            title: '',
            city: '',
            address: '',
            distance: '',
            photo: '',
            desc: '',
            price: '',
            maxGroupSize: '',
          });
    } catch (error) {
      console.error('Error adding tour:', error);
    }
  };

  return (
    <div className="add-tours-container">
      <h2>Add Tours</h2>
      <form onSubmit={handleSubmit}>
      <input
    type="text"
    name="title"
    value={tourData.title}
    onChange={handleChange}
    placeholder="Title"
    required
  />
  <input
    type="text"
    name="city"
    value={tourData.city}
    onChange={handleChange}
    placeholder="City"
    required
  />
  <input
    type="text"
    name="address"
    value={tourData.address}
    onChange={handleChange}
    placeholder="Address"
    required
  />
  <input
    type="number"
    name="distance"
    value={tourData.distance}
    onChange={handleChange}
    placeholder="Distance"
    required
  />
  <input
    type="text"
    name="desc"
    value={tourData.desc}
    onChange={handleChange}
    placeholder="Description"
    required
  />
  <input
    type="number"
    name="price"
    value={tourData.price}
    onChange={handleChange}
    placeholder="Price"
    required
  />
  <input
    type="number"
    name="maxGroupSize"
    value={tourData.maxGroupSize}
    onChange={handleChange}
    placeholder="Max Group Size"
    required
  />
         <input
          type="file"
          name="image"
          onChange={handleImageChange}
          accept="image/*"
          required
        />
        <button type="submit">Add Tour</button>
      </form>
    </div>
  );
};

export default AddTours;
