const AuthReducer = (state, action) => {
  switch (action.type) {
    case 'LOGIN_START':
      return {
        user: null,
        loading: true,
        error: null,
      };
    case 'LOGIN_SUCCESS':
      const user = action.payload;
      return {
        user,
        loading: false,
        error: null,
      };
    case 'LOGIN_FAILURE':
      return {
        user: null,
        loading: false,
        error: action.payload,
      };
    case 'REGISTER_SUCCESS':
      return {
        user: null,
        loading: false,
        error: null,
      };
    case 'LOGOUT':
      return {
        user: null,
        loading: false,
        error: null,
      };
    default:
      return state;
  }
};
// Example login logic
const login = async (credentials) => {
    try {
      // Simulate an API call to authenticate the user
      const user = await authenticateUser(credentials);
  
      if (user && user.role === 'admin') {
        user.isAdmin = true;
      }
  
      dispatch({ type: 'LOGIN_SUCCESS', payload: user });
      // ... other login logic ...
    } catch (error) {
      dispatch({ type: 'LOGIN_FAILURE', payload: error.message });
    }
  };
