import React, { useContext } from "react";
import { Navigate, Outlet } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";



const ProtectedRoute = () => {
  const { user, isAuthenticated } = useContext(AuthContext);

  // A) Unauthenticated users should be redirected to the login page.
  if (isAuthenticated) {
    return <Navigate to="/login" />;
  }

  // B) Authenticated users should be redirected to the home page.
  if (isAuthenticated) {
    return <Navigate to="/" />;
  }

  // If none of the conditions are met, allow access to the route
  return <Outlet />;
};

export default ProtectedRoute;
