import React, { useState, useEffect } from "react";
import { Container, Row, Col } from "reactstrap";
import axios from "axios";
import "../styles/booking.css";

const Bookingss = () => {
  const [bookings, setBookings] = useState([]);
  const [tours, setTours] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const bookingResponse = await axios.get(
        `http://localhost:4000/api/v1/booking`
      );
      setBookings(bookingResponse.data.data);

      // Fetch tour data
      const tourResponse = await axios.get(
        `http://localhost:4000/api/v1/tours`
      );
      setTours(tourResponse.data.data);

      setLoading(false);
    } catch (error) {
      console.error("Error fetching data:", error);
      setLoading(false);
    }
  };

  const handleDelete = async (bookingId) => {
    try {
      // Send a DELETE request to your API to delete the booking
      await axios.delete(`http://localhost:4000/api/v1/booking/${bookingId}`);
      // Remove the deleted booking from the local state
      setBookings((prevBookings) =>
        prevBookings.filter((booking) => booking._id !== bookingId)
      );
    } catch (error) {
      console.error("Error deleting booking:", error);
    }
  };

  return (
    <section>
      <Container>
        <Row>
          <Col lg="12">
            <h4 className="fw-bold">Bookings</h4>
          </Col>
          <Col lg="12">
            <table className="table">
              <thead>
                <tr>
                  <th>User Email</th>
                  <th>Full Name</th>
                  <th>Guest Size</th>
                  <th>Phone</th>
                  <th>BookAt</th>
                  <th>Actions</th>
                  {/* Add this column for tour details */}
                  <th>Tour Details</th>
                </tr>
              </thead>
              <tbody>
                {loading ? (
                  <tr>
                    <td colSpan="7">Loading...</td>
                  </tr>
                ) : (
                  bookings.map((booking) => (
                    <tr key={booking._id}>
                      <td>{booking.userEmail}</td>
                      <td>{booking.fullName}</td>
                      <td>{booking.guestSize}</td>
                      <td>{booking.phone}</td>
                      <td>{booking.bookAt}</td>
                      <td>
                        <button
                          onClick={() => handleDelete(booking._id)}
                          className="btn btn-danger"
                        >
                          Delete
                        </button>
                        {/* You can add more actions here */}
                      </td>
                      <td>
                        {/* Display tour details based on booking data */}
                        {tours.find((tour) => tour._id === booking._id)?.title }
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Bookingss;
