import React, { useState } from 'react';
import galleryImages from './galleryImages';
import Masonry, { ResponsiveMasonry } from 'react-responsive-masonry';

const MasonryImagesGallery = () => {
    const [selectedImage, setSelectedImage] = useState(null);
    const [isPopupOpen, setIsPopupOpen] = useState(false);

    const handleImageClick = (index) => {
        // Set the selected image based on the index clicked
        setSelectedImage(galleryImages[index]);
        // Open the popup
        setIsPopupOpen(true);
    };

    const closePopup = () => {
        // Clear the selected image and close the popup
        setSelectedImage(null);
        setIsPopupOpen(false);
    };

    return (
        <div>
            <ResponsiveMasonry columnsCountBreakPoints={{ 350: 1, 768: 3, 992: 4 }}>
                <Masonry gutter="1rem">
                    {galleryImages.map((item, index) => (
                        <div key={index}>
                            <img
                                className="masonry__img"
                                src={item}
                                alt=""
                                style={{ width: '100%', display: 'block', borderRadius: '10px' }}
                                onClick={() => handleImageClick(index)}
                            />
                          {selectedImage === galleryImages[index] && isPopupOpen && (
    <div className="image-popup">
        <div className="popup-content">
            <p>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                Fusce auctor, urna a tristique euismod, lorem sapien convallis diam,
                at malesuada erat leo id lorem.
            </p>
            <button className="close-button" onClick={closePopup}>Close</button>
        </div>
    </div>
)}

                           
                        </div>
                    ))}
                </Masonry>
            </ResponsiveMasonry>
        </div>
    );
};

export default MasonryImagesGallery;
