import React, { useContext, useEffect, useState } from "react";
import { Navigate, Outlet } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

const ProtectedRoute = () => {
  const { user, isAuthenticated } = useContext(AuthContext);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch the list of users from the backend when the component mounts
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await fetch("http://localhost:4000/api/v1/users/"); // Replace with your backend API endpoint
      if (response.ok) {
        const data = await response.json();
        setUsers(data);
      }
      setLoading(false);
    } catch (error) {
      console.error("Error fetching user data:", error);
    }
  };

  // Check if the user is authenticated
  if (!isAuthenticated) {
    // Redirect unauthenticated users to the login page
    return <Navigate to="/login" />;
  }

  // Check if the user has the "admin" role
  const isAdmin = users.some((u) => u._id === user._id && u.role === "admin");

  if (!isAdmin) {
    // If the user is not an admin, redirect to a restricted access page
    return <Navigate to="/restricted" />;
  }

  // If the user is an admin, allow access to the route
  return <Outlet />;
};

export default ProtectedRoute;
