import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Statistics = () => {
    const [tourCount, setTourCount] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Make an API request to fetch the tour count from your backend
        axios.get('http://localhost:4000/api/v1/searchgetTourCount')
            .then(response => {
                setTourCount(response.data.data);
                setLoading(false);
            })
            .catch(error => {
                console.error('Failed to fetch tour count:', error);
                setLoading(false);
            });
    }, []);

    return (
        <div>
            <h2>Tour Statistics</h2>
            {loading ? (
                <p>Loading...</p>
            ) : (
                <div>
                    <p>Total Tours: {tourCount}</p>
                    {/* You can add more statistics here */}
                </div>
            )}
        </div>
    );
}

export default Statistics;
