import { createContext, useEffect, useReducer } from 'react';

const initial_state = {
  user: null,
  loading: false,
  error: null,
  role: null,
};

export const AuthContext = createContext(initial_state);

const AuthReducer = (state, action) => {
  switch (action.type) {
    case 'LOGIN_START':
      return {
        user: null,
        loading: true,
        error: null,
        role: null,
      };
    case 'LOGIN_SUCCESS':
      return {
        user: action.payload.user,
        loading: false,
        error: null,
        role: action.payload.role,
      };
    case 'LOGIN_FAILURE':
      return {
        user: null,
        loading: false,
        error: action.payload,
        role: null,
      };
    case 'REGISTER_SUCCESS':
      return {
        user: null,
        loading: false,
        error: null,
        role: null,
      };
    case 'LOGOUT':
      return {
        user: null,
        loading: false,
        error: null,
        role: null,
      };
    default:
      return state;
  }
};

export const AuthContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(AuthReducer, initial_state);

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        dispatch({ type: 'LOGIN_SUCCESS', payload: { user: parsedUser, role: parsedUser.role } });
      } catch (error) {
        console.error('Error parsing user data from local storage:', error);
        localStorage.removeItem('user'); // Remove invalid data from storage
      }
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('user', JSON.stringify(state.user));
  }, [state.user]);

  return (
    <AuthContext.Provider
      value={{
        user: state.user,
        loading: state.loading,
        error: state.error,
        role: state.role,
        dispatch,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
};
