import React, { useContext } from "react";
import { Navigate, Outlet } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";

const ProtectedRoute = () => {
  const { isAuthenticated,user,role } = useContext(AuthContext);

  // A) Unauthenticated users should be redirected to the login page.
  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }

  if (user.role ==='admin') // B) Authenticated users should be redirected to the home page.
  return <Navigate to="/dashboard" />;

  // If none of the conditions are met, allow access to the route
  return <Outlet />;
};

export default ProtectedRoute;
