import {createContext, useEffect, useReducer} from 'react'

const initial_state = {
    user: localStorage.getItem('user')
      ? JSON.parse(localStorage.getItem('user'))
      : null,
    loading: false,
    error: null,
    role: null, // Add a role property
  };
  
 
export const AuthContext = createContext(initial_state)

const AuthReducer = (state,action)=>{
    switch(action.type){
        case'LOGIN_START':
        return{
            user:null,
    loading:true,
    error:null
        };
        case 'LOGIN_SUCCESS':
            return {
              user: action.payload.user,
              loading: false,
              error: null,
              role: action.payload.role, // Set the role from the action payload
            };
          
        case 'LOGIN_FAILURE':
            return{
                user:null,
             loading:false,
             error:action.payload
            }
            case 'REGISTER_SUCCESS':
            return{
                user:null,
              loading:false,
              error:null
            }
            
                  

            case 'LOGOUT':
  return {
    user: null,
    loading: false,
    error: null,
    role: null, // Reset the role when logging out
  };

        default :
        return state
    }
};

export const AuthContextProvider = ({children}) => {

    const [state, dispatch] = useReducer(AuthReducer, initial_state)

    useEffect(() =>{
    localStorage.setItem('user', JSON.stringify(state.user))
    },[state.user])

    return ( <AuthContext.Provider value={{
        user: state.user,
        loading: state.loading,
        error: state.error,
        dispatch,
         
    }}>
        {children}
        </AuthContext.Provider>
    );
}
