import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route } from 'react-router-dom'; // Import necessary routing components
import '../styles/users.css'; // Import your CSS file
import { Navigate } from 'react-router-dom';

const Users = () => {
  const [userRole, setUserRole] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchUserRole();
  }, []);

  const fetchUserRole = async () => {
    try {
      const response = await axios.get('http://localhost:4000/api/v1/user-role');
      const role = response.data.role; // Assuming the response contains the user's role
      setUserRole(role);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching user role:', error);
      setLoading(false);
    }
  };

  return (
   
      <div>
       
        {loading ? (
          <p>Loading user role...</p>
        ) : (
          <div>
            {userRole === 'u' ? (
              <div>
                <h2>Welcome, User!</h2>
                <p>You have access to the home page.</p>
               <Navigate to = "/home"/>
        
              </div>
            ) : (
              <div>
                <h2>Access Denied!</h2>
                <p>You do not have permission to access this page.</p>
              </div>
            )}
          </div>
        )}
      </div>

  );
};



export default Users;
