import React,{useState} from "react";
import { Container, Row, Col, Form, FormGroup } from "reactstrap";
import { db ,storage} from "../firebase.config";
import {ref, uploadBytesResumable,getDownloadURL} from "firebase/storage";
import {collection,addDoc} from "firebase/firestore";
import { useNavigate } from "react-router-dom";

const AddTours = () => {
  const [enterTitle, setEnterTitle] = useState('')
  const [enterShortDesc, setEnterShortDesc] = useState('')
  const [enterDescription, setEnterDescription] = useState('')
  const [enterCategory, setEnterCategory] = useState('')
  const [enterToursImg, setEnterToursImg] = useState('null')
  const [loading, setLoading] = useState(false)
  const navigate = useNavigate
  const addTours = async e => {
    e.preventDefault();
    setLoading(true)
    const Tours = {
      title: enterTitle,
      shortDesc: enterShortDesc,
      description: enterDescription,
      category: enterCategory,
      price: enterPrice,
      imgUrl: enterToursImg,
    
    };
    //=========add tours to the firebase database========//

    try {
      const docRef = collection(db, 'Tours')
      const storageRef = ref(storage, `ToursImages/${Date.now() +enterToursImg.name}`)
      const uploadTask = uploadBytesResumable(storageRef, setEnterToursImg)
  
      uploadTask.on(() => {
        toast.error('images not uploaded!')
      }, () => {
        getDownloadURL(uploadTask.snapshot.ref).then(async (getDownloadURL) => {
          await addDoc(docRef, {
            title: enterTitle,
            shortDesc: enterShortDesc,
            description: enterDescription,
            category: enterCategory,
            price: enterPrice,
            imgUrl: DownloadURL,
            
          
          
           })
        })
      }
     
    
    
    )


    setLoading(false)
    toast.success("tour successfully added");
    navigate("/dashbroad/all-tours");
  }
    catch (err) {
      toast.err("tour successfully not added!");
    }
  };
    return (
      <section>
        <Container>
          <Row>
            <Col lg="12">
              <h4 className="mb-5">Add Tours</h4>
              <Form onSubmit="addTours">
                <FormGroup className="form__group">
                  <span>Short Description</span>
                  <input
                    type="text"
                    placeholder="something here should be written1"
                    value={enterShortDesc}
                    onChange={(e) => setEnterTitle(e.target.value)}
                    required
                  />
                </FormGroup>

                <FormGroup className="form__group">
                  <span>Tours title</span>
                  <input
                    type="text"
                    placeholder="something here should be written2 "
                    value={enterTours}
                    onChange={(e) => setEnterTitle(e.target.value)}
                    required
                  />
                </FormGroup>
                <div>
                  <FormGroup className="form__group">
                    <span>Description</span>
                    <input
                      type="text"
                      placeholder="something here should be written3 "
                      value={(enterDescription = "here also")}
                      onChange={(e) => setEnterDesc(e.target.value)}
                      required
                    />
                  </FormGroup>

                  <FormGroup className="form__group w-50">
                    <span>Price</span>
                    <input
                      type="text"
                      placeholder="$100 "
                      value={enterPrice}
                      onChange={(e) => setEnterPrice(e.target.value)}
                      required
                    />
                  </FormGroup>

                  <FormGroup className="form__group w-50">
                    <Span>Category</Span>
                    <select
                      className="w-100 p-2"
                      value={enterCategory}
                      onChange={(e) => setEnterCategory}
                    >
                      <option value=" "> </option>
                      <option value=" "> </option>
                      <option value=" "> </option>
                      <option value=" "> </option>
                      <option value=" "> </option>
                    </select>
                  </FormGroup>
                </div>
                <div>
                  <FormGroup className="form__group">
                    <Span>Tours Image</Span>
                    <input type="file" onChange={e=>setEnterToursImg} placeholder="something here should be written "
                    ></input>
                  </FormGroup>
                </div>
                <button className="buy__btn">Add Tours</button>
              </Form>
            </Col>
          </Row>
        </Container>
      </section>
    );
  
  
  
  
  
};
export default AddTours;
