import React, { useState } from 'react';
import { toast } from 'react-toastify';
import "../styles/addtours.css";
import axios from 'axios';

const AddTours = () => {
  const [tourData, setTourData] = useState({
    title: '',
    city: '',
    address: '',
    distance: '',
    photo: '', // Assuming this should be a URL to an image
    desc: '',
    price: '',
    maxGroupSize: '',
    reviews: [], // An array of review IDs
    featured: false, // Optional, set to false by default
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTourData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:4000/tours/', tourData);

      if (response.status === 200) {
        toast.success('Tour package successfully added');
        console.log(response.data);

        // Reset form data
        setTourData({
          title: '',
          city: '',
          address: '',
          distance: '',
          photo: '',
          desc: '',
          price: '',
          maxGroupSize: '',
          reviews: [],
          featured: false,
        });
      } else {
        throw new Error('Network response was not ok');
      }
    } catch (error) {
      console.error('Error adding tour:', error);
    }
  };

  return (
    <div className="add-tours-container">
      <h2>Add Tours</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="title"
          value={tourData.title}
          onChange={handleChange}
          placeholder="Title"
          required
        />
        <input
          type="text"
          name="city"
          value={tourData.city}
          onChange={handleChange}
          placeholder="City"
          required
        />
        <input
          type="text"
          name="address"
          value={tourData.address}
          onChange={handleChange}
          placeholder="Address"
          required
        />
        <input
          type="number"
          name="distance"
          value={tourData.distance}
          onChange={handleChange}
          placeholder="Distance"
          required
        />
        <input
          type="file"
          name="photo"
          value={tourData.pho}
          onChange={handleImageChange}
          accept="image/*" // Accept only image files
          required
        />
        <input
          type="text"
          name="desc"
          value={tourData.desc}
          onChange={handleChange}
          placeholder="Description"
          required
        />
        <input
          type="number"
          name="price"
          value={tourData.price}
          onChange={handleChange}
          placeholder="Price"
          required
        />
        <input
          type="number"
          name="maxGroupSize"
          value={tourData.maxGroupSize}
          onChange={handleChange}
          placeholder="Max Group Size"
          required
        />
        <input
          type="text"
          name="reviews"
          value={tourData.reviews}
          onChange={handleChange}
          placeholder="Reviews"
          required
        />
        <label>
          Featured:
          <input
            type="checkbox"
            name="featured"
            checked={tourData.featured}
            onChange={() => setTourData({ ...tourData, featured: !tourData.featured })}
          />
        </label>

        <button type="submit">Add Tour</button>
      </form>
    </div>
  );
};

export default AddTours;
