import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from "reactstrap";
import axios from 'axios';
import { toast } from 'react-toastify';
import '../styles/alltours.css';

const AllTours = () => {
  const [tours, setTours] = useState([]);

  useEffect(() => {
    fetchTours();
  }, []);

  const fetchTours = async () => {
    try {
      const response = await axios.get('http://localhost:4000/tours/');
      setTours(response.data.data);
    } catch (error) {
      console.error('Error fetching tours:', error);
    }
  };

  const handleDelete = async (_id) => {
    try {
      const response = await axios.delete(`http://localhost:4000/tours/${_id}`);
      if (response.status === 204) {
        toast.success('Tour deleted successfully');
        fetchTours();
      } else {
        console.error('Error deleting tour. Unexpected status code:', response.status);
      }
    } catch (error) {
      console.error('Error deleting tour:', error);
      if (error.response) {
        console.error('Error response data:', error.response.data);
        console.error('Error response status:', error.response.status);
      }
    }
  };

  return (
    <Container className="all-tours-container">
      <h2>All Tours</h2>
      <Row>
        {tours.map((tour) => (
          <Col md={4} key={tour._id}>
            <div className="tour-card">
              <img src={tour.photo} alt={tour.title} className="tour-image" />
              <div className="tour-details">
                <h5>{tour.city}</h5>
                <h5>{tour.title}</h5>
                <h5>${tour.price} /per person</h5>
                <p>{tour.description}</p>
                <button onClick={() => handleDelete(tour._id)}>Delete</button>
              </div>
            </div>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default AllTours;
