import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom'; // Import necessary routing components
import '../styles/users.css'; // Import your CSS file

const Users = () => {
  const [userRole, setUserRole] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchUserRole();
  }, []);

  const fetchUserRole = async () => {
    try {
      const response = await axios.get('http://localhost:4000/api/v1/user-role');
      const role = response.data.role; // Assuming the response contains the user's role
      setUserRole(role);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching user role:', error);
      setLoading(false);
    }
  };

  return (
    <Router>
      <div>
        <h1>User Dashboard</h1>
        {loading ? (
          <p>Loading user role...</p>
        ) : (
          <div>
            {userRole === 'admin' ? (
              <div>
                <h2>Welcome, Admin!</h2>
                <p>You have access to everything.</p>
                <Route path="/" exact component={Home} />
                <Route path="/restricted" exact component={Restricted} />
              </div>
            ) : (
              <div>
                <h2>Welcome, User!</h2>
                <p>Your access is restricted.</p>
                <Redirect to="/home" />
              </div>
            )}
          </div>
        )}
      </div>
    </Router>
  );
};

const Home = () => <div>Welcome to the home page!</div>;

const Restricted = () => (
  <div>Welcome to the restricted page! Only admins can access this.</div>
);

export default Users;
