import React from 'react';
import Header from '../Header/Header';
import Routers from '../../router/Routers';
import Footer from '../Footer/Footer';
import AdminNav from '../../admin/AdminNav';
import { useLocation } from 'react-router-dom';

const Layout = () => {
  const location = useLocation();
  const user = { isAdmin: true }; // Replace with your user object or authentication logic

  return (
    <>
      {/* Render AdminNav only if the user is an admin and the path starts with '/dashboard' */}
      {user.isAdmin && location.pathname.startsWith('/dashboard') ? (
        <AdminNav />
      ) : (
        <Header />
      )}
      <Routers />
      <Footer />
    </>
  );
};

export default Layout;
