import React, { useState, useEffect } from "react";
import { Container, Row, Col } from "reactstrap";
import axios from "axios";
import { ExcelFile, ExcelSheet } from "react-data-export"; // Import ExcelFile and ExcelSheet
import "../styles/booking.css";

const Bookingss = () => {
  const [bookings, setBookings] = useState([]);
  const [tours, setTours] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const bookingResponse = await axios.get(
        `http://localhost:4000/api/v1/booking/`
      );
      setBookings(bookingResponse.data.data);

      // Fetch tour data
      const tourResponse = await axios.get(
        `http://localhost:4000/api/v1/tours`
      );
      setTours(tourResponse.data.data);

      setLoading(false);
    } catch (error) {
      console.error("Error fetching data:", error);
      setLoading(false);
    }
  };

  const handleDelete = async (bookingId) => {
    try {
      // Send a DELETE request to your API to delete the booking
      await axios.delete(`http://localhost:4000/api/v1/booking/${bookingId}`);
      // Remove the deleted booking from the local state
      setBookings((prevBookings) =>
        prevBookings.filter((booking) => booking._id !== bookingId)
      );
    } catch (error) {
      console.error("Error deleting booking:", error);
    }
  };

  // Prepare data for export
  const prepareDataForExport = () => {
    return bookings.map((booking) => ({
      "User Email": booking.userEmail,
      "Full Name": booking.fullName,
      "Guest Size": booking.guestSize,
      Phone: booking.phone,
      BookAt: booking.bookAt,
      "Tour Details": tours.find((tour) => tour.title === booking.tourName)?.city || "",
    }));
  };

  // Function to handle export
  const handleExport = () => {
    const dataForExport = prepareDataForExport();
    const fileName = "bookings.xlsx"; // Choose a file name

    const dataset = [
      {
        columns: [
          { title: "User Email", width: { wpx: 150 } },
          { title: "Full Name", width: { wpx: 150 } },
          { title: "Guest Size", width: { wpx: 100 } },
          { title: "Phone", width: { wpx: 100 } },
          { title: "Booked At", width: { wpx: 150 } },
          { title: "Tour Details", width: { wpx: 150 } },
        ],
        data: dataForExport,
      },
    ];

    const multiDataSet = [
      {
        columns: dataset,
      },
    ];

    // Create the ExcelFile component and trigger the download
    const excelFile = (
      <ExcelFile
        element={<button>Export to Excel</button>}
        filename={fileName}
      >
        <ExcelSheet dataSet={multiDataSet} name="Bookings" />
      </ExcelFile>
    );

    excelFile.save(fileName);
  };

  return (
    <section>
      <Container>
        <Row>
          <Col lg="12">
            <h4 className="fw-bold">Bookings</h4>
          </Col>
          <Col lg="12">
            <table className="table">
              <thead>
                <tr>
                  <th>User Email</th>
                  <th>Full Name</th>
                  <th>Guest Size</th>
                  <th>Phone</th>
                  <th>BookAt</th>
                  <th>Tour Details</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {loading ? (
                  <tr>
                    <td colSpan="7">Loading...</td>
                  </tr>
                ) : (
                  bookings.map((booking) => (
                    <tr key={booking._id}>
                      <td>{booking.userEmail}</td>
                      <td>{booking.fullName}</td>
                      <td>{booking.guestSize}</td>
                      <td>{booking.phone}</td>
                      <td>{booking.bookAt}</td>
                      <td>
                        {tours.find((tour) => tour.title === booking.tourName)?.city}
                      </td>
                      <td>
                        <button
                          onClick={() => handleDelete(booking._id)}
                          className="btn btn-danger"
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </Col>
        </Row>
        <Row>
          <Col lg="12">
            <button onClick={handleExport}>Export to Excel</button>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Bookingss;
