import { createContext, useReducer, useEffect } from 'react';
impo

const initialAuthState = {
  user: localStorage.getItem('user') ? JSON.parse(localStorage.getItem('user')) : null,
  loading: false,
  error: null,
  role: null,
};

const initialAdminState = {
  isAdmin: false,
  loading: false,
  error: null,
};

export const CombinedContext = createContext({});

const authReducer = (state, action) => {
  switch (action.type) {
    case 'LOGIN_START':
      return {
        ...state,
        user: null,
        loading: true,
        error: null,
      };
    case 'LOGIN_SUCCESS':
      return {
        ...state,
        user: action.payload,
        loading: false,
        error: null,
        role: null,
      };
    case 'LOGIN_FAILURE':
      return {
        ...state,
        user: null,
        loading: false,
        error: action.payload,
      };
    case 'REGISTER_SUCCESS':
      return {
        ...state,
        loading: false,
        error: null,
      };
    case 'LOGOUT':
      return {
        ...state,
        user: null,
        loading: false,
        error: null,
      };
    default:
      return state;
  }
};

const adminReducer = (state, action) => {
  switch (action.type) {
    case 'SET_ADMIN':
      return {
        ...state,
        isAdmin: action.payload,
      };
    case 'ADMIN_START':
      return {
        ...state,
        loading: true,
        error: null,
      };
    case 'ADMIN_SUCCESS':
      return {
        ...state,
        loading: false,
        error: null,
      };
    case 'ADMIN_FAILURE':
      return {
        ...state,
        loading: false,
        error: action.payload,
      };
    default:
      return state;
  }
};

export const CombinedContextProvider = ({ children }) => {
  const [authState, authDispatch] = useReducer(authReducer, initialAuthState);
  const [adminState, adminDispatch] = useReducer(adminReducer, initialAdminState);

  useEffect(() => {
    localStorage.setItem('user', JSON.stringify(authState.user));
  }, [authState.user]);

  return (
    <CombinedContext.Provider
      value={{
        auth: {
          user: authState.user,
          loading: authState.loading,
          error: authState.error,
          authDispatch,
        },
        admin: {
          isAdmin: adminState.isAdmin,
          loading: adminState.loading,
          error: adminState.error,
          adminDispatch,
        },
      }}
    >
      {children}
    </CombinedContext.Provider>
  );
};
