import {createContext, useEffect, useReducer} from 'react'

const initial_state = {
    user:localStorage.getItem('user') !== undefined 
    ? JSON.parse(localStorage.getItem('user'))
    :null,
    loading:false,
    error:null,
    role:null
}
 
export const AuthContext = createContext(initial_state)

const AuthReducer = (state,action)=>{
    switch(action.type){
        case'LOGIN_START':
        return{
            user:null,
    loading:true,
    error:null
        };
        case 'LOGIN_SUCCESS':
            return {
              user: action.payload.user,
              loading: false,
              error: null,
              role: action.payload.role, // Set the user's role here
            };
          // Example login action dispatch
dispatch({
    type: 'LOGIN_SUCCESS',
    payload: {
      user: userData, // User data retrieved after successful login
      role: 'admin', // Set the user's role here
    },
  });
  
        case 'LOGIN_FAILURE':
            return{
                user:null,
             loading:false,
             error:action.payload
            }
            case 'REGISTER_SUCCESS':
            return{
                user:null,
              loading:false,
              error:null
            }
            
                  

            case 'LOGOUT':
                return{
                    user:null,
                  loading:false,
                 error:null

                }
        default :
        return state
    }
};

export const AuthContextProvider = ({children}) => {

    const [state, dispatch] = useReducer(AuthReducer, initial_state)

    useEffect(() =>{
    localStorage.setItem('user', JSON.stringify(state.user))
    },[state.user])

    return ( <AuthContext.Provider value={{
        user: state.user,
        loading: state.loading,
        error: state.error,
        dispatch,
    }}>
        {children}
        </AuthContext.Provider>
    );
}
