import React from 'react';
import { Container, Row, Col, FormGroup, Input, Label } from 'reactstrap';
import axios from 'axios';
import { toast } from 'react-toastify';
import { Formik, Field, Form, ErrorMessage } from 'formik';
import * as Yup from 'yup';

const AddTours = () => {
  const initialValues = {
    title: '',
    city: '',
    address: '',
    distance: '',
    photo: '', // Assuming photo is a URL, you can change this to 'image' and handle file upload
    desc: '',
    price: '',
    maxGroupSize: '',
    reviews: [],
    featured: false, // Change to false for initial state
  };

  const validationSchema = Yup.object().shape({
    title: Yup.string().required('Title is required'),
    city: Yup.string().required('City is required'),
    address: Yup.string().required('Address is required'),
    distance: Yup.number().required('Distance is required'),
    photo: Yup.string().url('Invalid URL format'),
    desc: Yup.string().required('Description is required'),
    price: Yup.number().required('Price is required'),
    maxGroupSize: Yup.number().required('Max Group Size is required'),
  });

  const handleSubmit = async (values, { setSubmitting }) => {
    try {
      // Send a POST request to your backend API to add the tour
      const response = await axios.post('http://localhost:4000/tours', values);

      if (response.status === 200) {
        toast.success('Tour added successfully');
        // Reset the form data after successful addition
        setSubmitting(false);
      } else {
        throw new Error('Network response was not ok');
      }
    } catch (error) {
      console.error('Error adding tour:', error);
      toast.error('Failed to add tour');
      setSubmitting(false);
    }
  };

  return (
    <section>
      <Container>
        <Row>
          <Col lg="12">
            <h4 className="mb-5">Add Tours</h4>
            <Formik
              initialValues={initialValues}
              validationSchema={validationSchema}
              onSubmit={handleSubmit}
            >
              {({ isSubmitting, handleChange, values }) => (
                <Form>
                  <FormGroup>
                    <Label>Title</Label>
                    <Input type="text" name="title" onChange={handleChange} value={values.title} />
                    <ErrorMessage name="title" component="div" className="error" />
                  </FormGroup>

                  {/* Add similar FormGroup elements for other fields */}
                  
                  <FormGroup>
                    <Label>Photo (URL)</Label>
                    <Input type="text" name="photo" onChange={handleChange} value={values.photo} />
                    <ErrorMessage name="photo" component="div" className="error" />
                  </FormGroup>

                  <button className="buy__btn" type="submit" disabled={isSubmitting}>
                    Add Tour
                  </button>
                </Form>
              )}
            </Formik>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default AddTours;
