import React, { useContext } from "react";
import { Navigate, Outlet } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import { AdminContext } from "../context/AdminContext"; // Update the import path to match your project structure

const ProtectedRoute = () => {
  const { user, isAuthenticated } = useContext(AuthContext);
  const { isAdmin } = useContext(AdminContext);

  // A) Unauthenticated users should be redirected to the login page.
  if (!user ) {
    return <Navigate to="/" />;
  }

  // B) Authenticated non-admin users should be redirected to the home page.
  if (!isAdmin && !user) {
    return <Navigate to="/" />;
  }

  // C) Authenticated admin users should be allowed access to the route.
  return <Outlet />;
};

export default ProtectedRoute;
