import React, { useState, useEffect } from "react";
import { Container, Row, Col } from "reactstrap";
import axios from "axios";
import {Link} from 'react-router-dom';

const Bookingss = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchBookings();
  }, []);

  const fetchBookings = async () => {
    try {
      // Fetch data from the API
      const response = await axios.get("http://localhost:4000/bookings");
      
      // Check the response structure by console logging it
      console.log("Response:", response.data);
  
      // Assuming the data is nested under a "data" key, update the state
      setBookings(response.data.data);
  
      // Set loading to false since data fetching is complete
      setLoading(false);
    } catch (error) {
      console.error("Error fetching data:", error);
  
      // Set loading to false even if there is an error
      setLoading(false);
    }
  };
  

 

  return (
    <section>
      <Container>
        <Row>
          <Col lg="12">
            <h4 className="fw-bold">Bookings</h4>
          </Col>
          <Col lg="12" className="pt-5">
            <table className="table">
              <thead>
                <tr>
                  
                  <th>User Email</th>
                  <th>Full Name</th>
                  <th>Guest Size</th>
                  <th>Phone</th>
                  <th>Booked At</th>
                 
                </tr>
              </thead>
              <tbody>
                {loading ? (
                  <tr>
                    <td colSpan="6" className="text-center">
                      <h5 className="pt-5 fw-bold">Loading.......</h5>
                    </td>
                  </tr>
                ) : (
                  bookings.map((Booking) => (
                    <tr key={Booking._id}>
                      <td>{Booking.userEmail}</td>
                      <td>{Booking.fullName}</td>
                      <td>{Booking.guestSize}</td>
                      <td>{Booking.phone}</td>
                      <td>{new Date(Booking.bookAt).toLocaleString()}</td>
                      <td>
                        
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Bookingss;
