import React, {useContext} from 'react';
import { Container,Row } from 'reactstrap';
import { AuthContext } from '../context/AuthContext';
import userIcon from '../assets/images/user.png';
import "../styles/admin-nav.css";
import { NavLink } from 'react-router-dom';


const admin__nav = [
    {
        display: 'Dashboard',
        path:'/dashboard'
    },
    {
        display: 'All-Tours',
        path:'/dashboard/all-tours'
    },
    {
      display: 'Add-Tours',
      path:'/dashboard/add-tours'
  },

    {
        display: 'Bookings',
        path:'/dashboard/bookings'
    },
    {
        display: 'Users',
        path:'/dashboard/users'
    },
]



const AdminNav = () => {

    const {user} = useContext(AuthContext);

    return (
        <>
        <header className='admin__header'>
            <div className='admin__nav-top'>
                <Container>
<div className='admin__nav-wrapper-top'>
    <div className='logo'>
        <h4>TravelWorld</h4>
</div>
<div className='search__box d-flex align-items-center'>
  <input type="text" placeholder='Search.....' style={{ width: '900px' ,height: '30px'}} />
  <span>
    <i className="ri-search-line"></i>
  </span>
</div>



<div className="admin__nav-top-right">
    <span>
    <i class="ri-notification-4-line"></i>
    </span>
    <span><i class="ri-settings-3-line"></i></span>
    <img src={userIcon} alt="" />
</div>
    </div>
    </Container>
</div>
        </header>
<section className='admin__menu p-0'>
    <Container>
        <Row>
        <div className="admin__navigation">
  <ul className='admin__menu-list' style={{ display: 'flex', gap: '10px' }}>
    {
      admin__nav.map((item, index) => (
        <li className="admin__menu-item" key={index}>
          <NavLink to={item.path} className={navClass=> navClass.isActive ? 'active__admin-menu ': ''}>{item.display}</NavLink>
        </li>
      ))
    }
  </ul>
</div>

        </Row>
    </Container>

</section>

        </>
    );
}

export default AdminNav;
