import React, { useState, useEffect } from "react";
import { Container, Row, Col } from "reactstrap";
import axios from "axios";

const Bookingss = () => {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    
    fetchBookings();
  }, []);

    const fetchBookings = async () => {
      try {
        const response = await axios.get("http://localhost:4000/bookings/");
        setBookings(response.data.data); // Assuming your data is nested under a "data" key
        
      } catch (error) {
        console.error("Error fetching data:", error);
      
      }
    };

  const handleDeleteBooking = async (bookingId) => {
    try {
      // Send a DELETE request to remove the booking with the specified ID
      await axios.delete(`http://localhost:4000/bookings/${bookingId}`);
      // Update the bookings list by removing the deleted booking
      setBookings((prevBookings) => prevBookings.filter((booking) => booking._id !== bookingId));
    } catch (error) {
      console.error('Error deleting booking:', error);
    }
  };

  return (
    <section>
      <Container>
        <Row>
          <Col lg="12">
            <h4 className="fw-bold">Bookings</h4>
          </Col>
          <Col lg="12" className="pt-5">
            <table className="table">
              <thead>
                <tr>
                  <th>User Email</th>
                  <th>Full Name</th>
                  <th>Guest Size</th>
                  <th>Phone</th>
                  <th>Booked At</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
              
              
            {
                  bookings.map((Booking) => (
                    <tr key={Booking._id}>
                      <td>{Booking.userEmail}</td>
                      <td>{booking.fullName}</td>
                      <td>{booking.guestSize}</td>
                      <td>{booking.phone}</td>
                      <td>{new Date(booking.bookAt).toLocaleString()}</td>
                      <td>
                        <button
                          onClick={() => handleDeleteBooking(booking._id)}
                          className="btn btn-danger"
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))
                }
              </tbody>
            </table>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Bookingss;
