import React, { useState } from 'react';
import { toast } from 'react-toastify';
import "../styles/addtours.css";
import { BASE_URL } from '../utils/config';
import axios from 'axios';

const AddTours = () => {
  const [tourData, setTourData] = useState({
    title: '',
    city: '',
    address: '',
    distance: '',
    price: '',
    maxGroupSize: '',
    desc: '',
    reviews: '',
    image: null,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setTourData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleImageChange = (e) => {
    const imageFile = e.target.files[0];
    setTourData((prevData) => ({
      ...prevData,
      image: imageFile,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append('title', tourData.title);
      formData.append('city', tourData.city);
      formData.append('address', tourData.address);
      formData.append('distance', tourData.distance);
      formData.append('price', tourData.price);
      formData.append('maxGroupSize', tourData.maxGroupSize);
      formData.append('desc', tourData.desc);
      formData.append('reviews', tourData.reviews);
      formData.append('image', tourData.image);

      const response = await axios.post(  "http://localhost:4000/tours/", {
        method: 'POST',
        body: formData,
      });
      
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      toast.success('Tour package successfully added');
      console.log(response.data);

      // Reset form data
      setTourData({
        title: '',
        city: '',
        address: '',
        distance: '',
        image: null,
        desc: '',
        price: '',
        maxGroupSize: '',
       
        reviews: '',
       
      });
    } catch (error) {
      console.error('Error adding tour:', error);
    }
  };
  return (
    <div className="add-tours-container">
      <h2>Add Tours</h2>
      <form onSubmit={handleSubmit}>
      <input
    type="text"
    name="title"
    value={tourData.title}
    onChange={handleChange}
    placeholder="Title"
    required
  />
  <input
    type="text"
    name="city"
    value={tourData.city}
    onChange={handleChange}
    placeholder="City"
    required
  />
  <input
    type="text"
    name="address"
    value={tourData.address}
    onChange={handleChange}
    placeholder="Address"
    required
  />
  <input
    type="number"
    name="distance"
    value={tourData.distance}
    onChange={handleChange}
    placeholder="Distance"
    required
  />
  
  <input
    type="text"
    name="desc"
    value={tourData.desc}
    onChange={handleChange}
    placeholder="Description"
    required
  />
  <input
    type="number"
    name="price"
    value={tourData.price}
    onChange={handleChange}
    placeholder="Price"
    required
  />
  <input
    type="number"
    name="maxGroupSize"
    value={tourData.maxGroupSize}
    onChange={handleChange}
    placeholder="Max Group Size"
    required
  />
       
        <button type="submit" >Add Tour</button>

      </form>
    </div>
  );
};

export default AddTours;
