// ExportToExcel.jsx
import React from 'react';
import XLSX from 'xlsx';

// Function to export bookings to Excel
const exportToExcel = () => {
    const dataset = bookings.map((booking) => ({
      UserEmail: booking.userEmail,
      FullName: booking.fullName,
      GuestSize: booking.guestSize,
      Phone: booking.phone,
      BookAt: booking.bookAt,
      TourDetails: tours.find((tour) => tour.title === booking.tourName)?.city,
    }));
  
    const data = [
      {
        columns: [
          { title: "User Email", width: { wpx: 150 } },
          { title: "Full Name", width: { wpx: 150 } },
          { title: "Guest Size", width: { wpx: 100 } },
          { title: "Phone", width: { wpx: 100 } },
          { title: "BookAt", width: { wpx: 150 } },
          { title: "Tour Details", width: { wpx: 150 } },
        ],
        data: dataset,
      },
    ];
  
    return (
      <ExcelFile filename="bookings">
        <ExcelSheet dataSet={data} name="bookings" />
      </ExcelFile>
    );
  };
