import React, { useState } from 'react';
import galleryImages from './galleryImages';
import Masonry, { ResponsiveMasonry } from 'react-responsive-masonry';
import './image-description.css'; // Make sure to import your CSS file

const MasonryImagesGallery = () => {
    const [selectedImage, setSelectedImage] = useState(null);
    const [selectedDescription, setSelectedDescription] = useState(null);

    const handleImageClick = (index) => {
        // Set the selected image and description based on the index clicked
        setSelectedImage(galleryImages[index]);
        setSelectedDescription('Description specific to the selected image goes here.');
    };

    const closePopup = () => {
        // Clear the selected image and description
        setSelectedImage(null);
        setSelectedDescription(null);
    };

    return (
        <div>
            <ResponsiveMasonry columnsCountBreakPoints={{ 350: 1, 768: 3, 992: 4 }}>
                <Masonry gutter="1rem">
                    {galleryImages.map((item, index) => (
                        <div key={index} className="masonry-item">
                            <img
                                className="masonry__img"
                                src={item}
                                alt=""
                                onClick={() => handleImageClick(index)}
                            />
                        </div>
                    ))}
                </Masonry>
            </ResponsiveMasonry>

            {selectedImage && (
                <div className="image-popup">
                    <div className="popup-content">
                        <img className="popup-image" src={selectedImage} alt="" />
                        <p className="popup-description">
                            {selectedDescription}
                        </p>
                        <button className="close-button" onClick={closePopup}>Close</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default MasonryImagesGallery;
