import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import '../styles/booking.css';

const Booking = () => {
  const { tourId } = useParams();
  const [tour, setTour] = useState(null);
  const [bookings, setBookings] = useState([]);
  const [bookingData, setBookingData] = useState({
    name: '',
    email: '',
    participants: 1,
  });

  useEffect(() => {
    fetchTour();
    fetchBookingsForTour();
  }, []);

  const fetchTour = async () => {
    try {
      const response = await axios.get(`http://localhost:4000/tours/`);
      setTour(response.data.data);
    } catch (error) {
      console.error('Error fetching tour:', error);
    }
  };

  const fetchBookingsForTour = async () => {
    try {
      const response = await axios.get(`http://localhost:4000/bookings/tour/`);
      setBookings(response.data.data);
    } catch (error) {
      console.error('Error fetching bookings:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Make a POST request to create a new booking
      const response = await axios.post(`http://localhost:4000/bookings/`, bookingData);
      if (response.data.success) {
        alert('Booking successful!');
        // You can redirect or handle success as needed
      }
    } catch (error) {
      console.error('Error booking tour:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setBookingData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  if (!tour) {
    return <div>Loading...</div>;
  }

  return (
    <div className="booking-container">
      <h2>Book {tour.title}</h2>
      <form onSubmit={handleSubmit}>
        {/* Form fields for booking data */}
        {/* ... (same as in your original code) */}
        <button type="submit">Book Now</button>
      </form>

      {/* Display the list of bookings */}
      <div className="booking-list">
        <h3>Bookings for {tour.title}</h3>
        <ul>
          {bookings.map((booking) => (
            <li key={booking._id}>
              Name: {booking.name}, Email: {booking.email}, Participants: {booking.participants}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Booking;
