import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Navigate } from 'react-router-dom';

const Users = () => {
  const [userRole, setUserRole] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchUserRole();
  }, []);

  const fetchUserRole = async () => {
    try {
      const response = await axios.get('http://localhost:4000/api/v1/user-role');
      const role = response.data.role; // Assuming the response contains the user's role
      setUserRole(role);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching user role:', error);
      setLoading(false);
    }
  };

  // Conditionally render the Navigate component based on userRole
  if (loading) {
    return <div>Loading...</div>;
  } else if (userRole === 'user') {
    return <Navigate to="/home" />;
  } else {
    // Render something else for other roles or cases
    return <div>Not authorized</div>;
  }
};

export default Users;
