import React, { useState } from "react";
import { Container, Row, Col, Form, FormGroup } from "reactstrap";
import { MongoClient } from "mongodb"; // Import MongoDB driver
import { useNavigate } from "react-router-dom";

const mongoURI = "mongodb://localhost:27017"; // Replace with your MongoDB URI
const dbName = "your-database-name"; // Replace with your database name

const AddTours = () => {
  const [enterTitle, setEnterTitle] = useState("");
  const [enterShortDesc, setEnterShortDesc] = useState("");
  const [enterDescription, setEnterDescription] = useState("");
  const [enterCategory, setEnterCategory] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const addTours = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const client = new MongoClient(mongoURI);
      await client.connect();

      const database = client.db(dbName);
      const toursCollection = database.collection("tours");

      const tourDocument = {
        title: enterTitle,
        shortDesc: enterShortDesc,
        description: enterDescription,
        category: enterCategory,
      };

      // Insert the document into the collection
      await toursCollection.insertOne(tourDocument);

      setLoading(false);
      console.log("Tour successfully added");
    } catch (error) {
      console.error("Error adding tour:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <section>
      <Container>
        <Row>
          <Col lg="12">
            <h4 className="mb-5">Add Tours</h4>
            <Form onSubmit={addTours}>
              <FormGroup className="form__group">
                <span>Short Description</span>
                <input
                  type="text"
                  placeholder="Enter short description"
                  value={enterShortDesc}
                  onChange={(e) => setEnterShortDesc(e.target.value)}
                  required
                />
              </FormGroup>
              
              {/* Other form inputs */}
              
              <div>
                <FormGroup className="form__group">
                  <span>Tours Image</span>
                  <input
                    type="file"
                    onChange={(e) => setEnterToursImg(e.target.files[0])}
                    placeholder="Choose an image"
                  />
                </FormGroup>
              </div>
              <button className="buy__btn" type="submit">Add Tours</button>
            </Form>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default AddTours;
