import React, {useEffect,useRef, useState, useContext} from "react";
import '../styles/tour-details.css'	
import { Container, Row, Col, Form, ListGroup } from "reactstrap"
import { useParams } from "react-router-dom" 	;
import calculateAvgRating from './../utils/avgRating';
import avatar from '../assets/images/avatar.jpg';
import Booking from "../components/Booking/Booking";
import Newsletter from "../shared/Newsletter";
import useFetch from './../hooks/useFetch';
import { BASE_URL} from './../utils/config';
import {AuthContext} from "./../context/AuthContext";

const TourDetails = () => {
    
    const { id } = useParams();
    const reviewMsgRef = useRef("");
    const [tourRating,setTourRating]=useState(null);
const {user} = useContext(AuthContext)
    
    const {data:tour, loading, error} = useFetch(`${BASE_URL}/tours/${id}`);
    
  
    const { photo , title, desc, price, address, reviews, city, distance, maxGroupSize } = tour;
   
    const {totalRating, avgRating} = calculateAvgRating (reviews);

    const options = {day: 'numeric',month: "long", year:"numeric"};

    const submitHandler = async e => {
        e.preventDefault();
        const reviewText =reviewMsgRef.current.value;

       
        
        try {

            if(!user || user===undefined || user===null){

                alert('please sign in')
            }
            const reviewObj = {
                userName: user?.username,
                reviewText,
                rating: tourRating
            }

            const res = await fetch(`${BASE_URL}/review/${id}`,{
                method:'post',
                headers:{
                    'content-type':'application/jason'
                },
                    credentials:'include',
                    body:JSON.stringify(reviewObj)

            })
            const result= await res.jason()
            if(!res.ok) {
                return alert(result.message);
            }
            alert(result.message)
            
        } catch (err) {
            alert(err.message);
        }
    
    };
useEffect(()=>{
    window.scrollTo(0,0)
},[tour])

      return (
        <>
        <section>
            <Container>
                {
                    loading && <h4 className="text-center pt-5">Loading....</h4>
}
                {
                    error && <h4 className="text-center pt-5">{error}</h4>
                }
                {
                    !loading && !error &&  
                    <Row>
                    <Col lg= "8">
                       <div className="tour__content">
                          <img src={photo} alt=""/>
                        
                          <div className="tour__info">
                            <h2>{title}</h2>

                            <div className="d-flex align-items-center gap-5">
                                <span className="tour__rating d=flex align-items-center gap-1">
                                    <i class="ri-star-s-fill" style={{'color':"var(--secondary-color)"}}></i> 
                                    {avgRating === 0 ? null : avgRating}
                                    {totalRating === 0 ? (
                                        "Not rated" 
                                    ) : (
                                       <span> ({reviews?.length}) </span>
                                    )}
                                    </span>
                                <span>
                                <i class="ri-map-pin-user-line"></i> {address}
                                </span>
                                 </div>
                                 <h5>Description</h5>
                                 <p>{desc}</p>
                          
                            <div className="tour__extra-details">
                              <span><i class="ri-map-pin-add-line"></i>{city}</span>
                              <span><i class="ri-money-dollar-circle-line"></i> ${price} /per person</span>
                              <i class="ri-map-pin-time-line"></i> {distance} k/m
                              <span><i class="ri-group-line"></i>{maxGroupSize} people </span>
                            </div>
                            <h5>Description</h5>
                                 <p>{desc}</p>
                                 </div>

                            {/*==========tour reviews section========== */}

                            <div className="tour__review mt-4">

                                <h4>Reviews({reviews?.length} reviews)</h4>
                                <Form onSubmit={submitHandler}>
                                    <div className="d-flex align-items-center gap-3 mb-4 
                                    rating__group">
                                       1 <span onClick={() => setTourRating(1)}>
                                        <i class="ri-star-s-fill"></i>
                                       </span>
                                       2 <span onClick={() => setTourRating(2)}>
                                        <i class="ri-star-s-fill"></i>
                                        </span>
                                       3 <span onClick={() => setTourRating(3)}>
                                        <i class="ri-star-s-fill"></i>
                                        </span>
                                       4 <span onClick={() => setTourRating(4)}>
                                        <i class="ri-star-s-fill"></i>
                                        </span>
                                       5 <span onClick={() => setTourRating(5)}>
                                        <i class="ri-star-s-fill"></i>
                                        </span>
                                    </div>
                                    
                                    <div className="review__input">
                                        <input type="text" ref={reviewMsgRef} placeholder="Share your thoughts"
                                        required 
                                        />
                                            <button className="btn primary__btn text-white" type="submit"
                                            >
                                                Submit
                                            </button>
                                    </div>
                                </Form>

                                <ListGroup className="user__reviews">
                                    {
                                        reviews?.map (review=>(
                                            <div className="review__item">
                                                <img src={avatar} alt="" />

                                                <div className="w-100">
                                                    <div className="d-flex align-items-center justify-content-between">
                                                        <div>
                                                            <h5>{review.username}</h5>
                                                            <p>{new Date(review.createdAt
                                                            ).toLocaleDateString("en-US", options
                                                            )}</p>
                                                        </div>
                                                        <span className="d-flex align-items-center">
                                                            {review.rating}
                                                            <i class="ri-star-s-fill"></i>
                                                        </span>
                                                    </div>

                                                    <h6>{review.reviewText}</h6>
                                                </div>
                                            </div>
                                        )) 
                                    }

                                </ListGroup>
                                </div>
                            
                            {/*==========tour reviews section ends ==========*/}
                          
                        
                        </div>
                    </Col>
                    <Col lg='4'>
                    <Booking tour={tour} avgRating={avgRating} />
                    </Col>
                </Row>
                    
                }
            </Container>
         </section>
         <Newsletter/>
        </>

    );

};
    
export default TourDetails;
